import { HttpErrorResponse } from '@angular/common/http';
import { Component, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { Router } from '@angular/router';
import { NotifierService } from 'angular-notifier';
import { catchError, throwError } from 'rxjs';
import { AuthService } from 'src/app/Services/Auth/auth.service';
import { EventService } from 'src/app/Services/Event/event.service';
import { FamilyService } from 'src/app/Services/Family/family.service';
import { MemberService } from 'src/app/Services/Member/member.service';
import { FamilyDialogComponent } from '../../dialogs/family-dialog/family-dialog.component';
import { AppService } from 'src/app/Services/app/app.service';

@Component({
  selector: 'app-detail-family',
  templateUrl: './detail-family.component.html',
  styleUrls: ['./detail-family.component.css']
})
export class DetailFamilyComponent {

  listeMembre: any[] = [];
  listeEvents: any[] = [];
  displayedColumnsMember: string[] = ['nom', 'sexe', 'date', 'lieu', 'action'];
  dataSource = new MatTableDataSource<any>(this.listeMembre);

  constructor(
    protected _appService: AppService,
    private _memberService: MemberService,
    private _authService: AuthService,
    protected _familyService: FamilyService,
    private _eventService: EventService,
    private _notifierService: NotifierService,
    protected _router: Router,
    private _dialog: MatDialog
  ) {
    _familyService.getCurrent();
  }

  @ViewChild(MatPaginator) paginator!: MatPaginator;

  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
  }

  /**
   * Handle get family members request
   */
  getFamilieMember() {
    try {
      this._memberService.getFamilyMembers(this._familyService.currentFamily.id, this._authService.user.token).pipe(
        catchError((error: HttpErrorResponse) => {
          if (error.status !== 200) {
            // display error
            this._notifierService.notify('error', 'Une erreur est survenue.');
            console.error(error);
          }
          return throwError(error);
        })
      ).subscribe(res => {
        // get response
        this.listeMembre = res;

        console.log(res);


        // set data
        this.dataSource = new MatTableDataSource<any>(this.listeMembre);
      });

    } catch (error) {
      // log error
      this._notifierService.notify('error', 'Une erreur est survenue');

      // log
      console.error(error);

    }

  }

  /**
   * Handle get families events request
   */
  getEvents() {
    try {
      this._eventService.getFamilyEvents(this._familyService.currentFamily.id, this._authService.user.token).pipe(
        catchError((error: HttpErrorResponse) => {
          if (error.status !== 200) {
            // display error
            this._notifierService.notify('error', 'Une erreur est survenue.');
            console.error(error);
          }
          return throwError(error);
        })
      ).subscribe(res => {
        // get response
        this.listeEvents = res.data;
      });

    } catch (error) {
      // log error
      this._notifierService.notify('error', 'Une erreur est survenue');

      // log
      console.error(error);

    }
  }

  openFamilyDialog() {
    let dialog = this._dialog.open(FamilyDialogComponent, {
      data: {
        mode: 'edit',
        element: this._familyService.currentFamily
      },
    });

    dialog.afterClosed().subscribe(result => {
      console.log(result);

    });
  }

  openMemberDialog(mode: string, element: any) {
    let dialog = this._dialog.open(FamilyDialogComponent, {
      data: {
        mode: 'edit',
        element: this._familyService.currentFamily
      },
    });

    dialog.afterClosed().subscribe(result => {
      console.log(result);

    });
  }

  ngOnInit() {
    this._familyService.getCurrent();
    this.getFamilieMember();
    console.log(this._familyService.currentFamily);

  }

}
<!-- title -->
<div class="mb-3">
  <button
    (click)="_router.navigateByUrl('/home/family')"
    mat-button
    class="bg-light small rounded-2"
  >
    <mat-icon>arrow_back</mat-icon>
    Retour
  </button>
</div>
<div
  class="text-dark mb-2 sticky-top t-86 d-flex align-items-center bg-white pb-1 mb-2"
  style="z-index: 996 !important"
>
  <button
    data-bs-toggle="offcanvas"
    data-bs-target="#offcanvasExample"
    aria-controls="offcanvasExample"
    mat-icon-button
    class="me-2 d-lg-none"
  >
    <mat-icon>sort</mat-icon>
  </button>
  <!-- img -->
  <img
    src="{{
      _appService.baseUrl + '/' + _familyService.currentFamily.photo_profil_path
    }}"
    alt=""
    srcset=""
    class="rounded-pill me-3 img-fluid"
    style="width: 90px; height: 90px; cursor: pointer"
  />
  <span class="flex-fill fs-4"> {{ _familyService.currentFamily.name }} </span>
  <button
    mat-stroked-button
    (click)="openFamilyDialog()"
    class="ms-2 border-primary"
    color="primary"
  >
    Modifier
  </button>
</div>

<!-- detail famille -->
<div class="mb-3"></div>

<div class="">
  <mat-tab-group
    color="warn"
    disableRipple="true"
    mat-stretch-tabs="false"
    mat-align-tabs="start"
  >
    <mat-tab label="Membres">
      <div class="mt-4">
        <!-- text -->
        <div
          class="mb-3 text-secondary d-flex align-items-center flex-row justify-content-between"
        >
          <button
            mat-stroked-button
            color="warn"
            class="btn-sm small border-danger"
          >
            Ajouter un membre
          </button>
        </div>

        <!-- content -->
        <table mat-table [dataSource]="dataSource" class="">
          <!-- nom Column -->
          <ng-container matColumnDef="nom">
            <th mat-header-cell *matHeaderCellDef>Nom</th>
            <td mat-cell *matCellDef="let element">{{ element.name }}</td>
          </ng-container>

          <!-- sexe Column -->
          <ng-container matColumnDef="sexe">
            <th mat-header-cell *matHeaderCellDef>Sexe</th>
            <td mat-cell *matCellDef="let element" class="text-capitalize">
              {{ element.sexe }}
            </td>
          </ng-container>

          <!-- date Column -->
          <ng-container matColumnDef="date">
            <th mat-header-cell *matHeaderCellDef>Date de naissance</th>
            <td mat-cell *matCellDef="let element">Default</td>
          </ng-container>

          <!-- lieu Column -->
          <ng-container matColumnDef="lieu">
            <th mat-header-cell *matHeaderCellDef>Lieu de naissance</th>
            <td mat-cell *matCellDef="let element">Default</td>
          </ng-container>

          <!-- action Column -->
          <ng-container matColumnDef="action">
            <th mat-header-cell *matHeaderCellDef>Actions</th>
            <td mat-cell *matCellDef="let element">
              <button class="btn btn-sm btn-outline-warning me-2">Voir</button>
              <button class="btn btn-sm btn-outline-success me-2">
                Consulter
              </button>
              <button class="btn btn-sm btn-outline-danger">Supprimer</button>
            </td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="displayedColumnsMember"></tr>
          <tr
            mat-row
            *matRowDef="let row; columns: displayedColumnsMember"
          ></tr>
        </table>

        <!-- paginator -->
        <mat-paginator
          class="mt-3"
          [pageSizeOptions]="[5, 10, 20]"
          showFirstLastButtons
          aria-label="Select page of periodic elements"
        >
        </mat-paginator>
      </div>
    </mat-tab>
    <mat-tab label="Événements">
      <div class="mt-4">
        <!-- lister les evenements -->
      </div>
    </mat-tab>
  </mat-tab-group>
</div>
